#include <assert.h>

#include <objectively/${file_base}.h>

#pragma mark - Object instance methods

/**
 * @see Object::dealloc(Object *)
 */
static void dealloc(Object *self) {

	super(Object, self, dealloc);
}

/**
 * @see Object::init(id, id, va_list *)
 */
static Object *init(id obj, id interface, va_list *args) {

	${file_base} *self = (${file_base} *) super(Object, self, init, interface, args);
	if (self) {
		self->interface = (${file_base}Interface *) interface;

		//..
	}

	return self;
}

#pragma mark - ${file_base} instance methods

//..

#pragma mark - ${file_base} Class methods

static void initialize(Class *self) {

	((ObjectInterface *) self->interface)->dealloc = dealloc;
	((ObjectInterface *) self->interface)->init = init;

	//..
}

Class __${file_base} = {
	.name = "${file_base}",
	.superclass = __Object,
	.instanceSize = sizeof(${file_base}),
	.interfaceSize = sizeof(${file_base}Interface),
	.initialize = initialize,
};


#endif

